        // 通用：更新侧边栏计数器
        function updateActivitySidebarCount(type) {
            const cls = type.replace(/_/g, '-');
            const counterEl = document.querySelector(`#sidebar-item-${type} + label .${cls}-counter`);
            if (!counterEl) return;
            const total = Array.isArray(window.itemData?.[type]) ? window.itemData[type].length : 0;
            const vm = VisibilityCache.loadVisibility(type) || {};
            let hidden = 0;
            for (let i = 0; i < total; i++) if (vm[i] === true) hidden++;
            counterEl.textContent = total > 0 ? ` (${hidden}/${total})` : '';
        }

