        // 通用：应用可见性缓存（设置透明度并刷新计数器）
        function applyActivityVisibilityCache(type) {
            try {
                const camel = type.replace(/_([a-z])/g, (m, c) => c.toUpperCase());
                const markerArrayName = `${camel}Markers`;
                const markers = (window[markerArrayName] && window[markerArrayName]) || [];
                const vm = VisibilityCache.loadVisibility(type) || {};
                if (Array.isArray(markers) && markers.length > 0) {
                    markers.forEach((marker, i) => {
                        if (!marker) return;
                        const isHidden = !!vm[i];
                        const opacity = isHidden ? 0.3 : 1;
                        if (typeof marker.setOpacity === 'function') marker.setOpacity(opacity);
                        else if (typeof marker.setStyle === 'function') marker.setStyle({ opacity });
                        else if (typeof marker.getElement === 'function') { const el = marker.getElement(); if (el) el.style.opacity = String(opacity); }
                    });
                }
                updateActivitySidebarCount(type);
            } catch (e) { console.warn(`应用 ${type} 可见性缓存失败:`, e); }
        }

