        // 侧边栏计数器：armored_truck_sp（基于 VisibilityCache 统计隐藏/总数）
        function updateArmoredTruckSpSidebarCount() {
            try {
                let counterEl = document.querySelector('#sidebar-item-armored_truck_sp + label .armored-truck-sp-counter');
                // 兜底：通过 nextElementSibling 精确定位 label 再找 counter
                if (!counterEl) {
                    const inputEl = document.getElementById('sidebar-item-armored_truck_sp');
                    const labelEl = inputEl && inputEl.nextElementSibling;
                    if (labelEl) counterEl = labelEl.querySelector('.armored-truck-sp-counter');
                }
                if (!counterEl) return;
                const total = Array.isArray(window.itemData?.armored_truck_sp) ? window.itemData.armored_truck_sp.length : 0;
                const vm = VisibilityCache.loadVisibility('armored_truck_sp') || {};
                let hidden = 0;
                for (let i = 0; i < total; i++) {
                    if (vm[i] === true) hidden++;
                }
                counterEl.textContent = total > 0 ? ` (${hidden}/${total})` : '';
            } catch (e) {
                console.warn('更新 armored_truck_sp 计数器失败:', e);
            }
        }

        // 应用装甲运钞车（线下）可见性缓存（设置透明度 + 刷新计数器）
        function applyArmoredTruckSpVisibilityCache() {
            try {
                const markers = (window.armoredTruckSpMarkers && window.armoredTruckSpMarkers) || [];
                const visibilityMap = VisibilityCache.loadVisibility('armored_truck_sp') || {};
                if (Array.isArray(markers) && markers.length > 0) {
                    let appliedHidden = 0;
                    markers.forEach((marker, i) => {
                        if (!marker) return;
                        const isHidden = !!visibilityMap[i];
                        const opacity = isHidden ? 0.3 : 1;
                        if (typeof marker.setOpacity === 'function') marker.setOpacity(opacity);
                        else if (typeof marker.setStyle === 'function') marker.setStyle({ opacity });
                        else if (typeof marker.getElement === 'function') {
                            const el = marker.getElement();
                            if (el) el.style.opacity = String(opacity);
                        }
                        if (isHidden) appliedHidden++;
                    });
                }
                try { updateArmoredTruckSpSidebarCount(); } catch (e) {}
            } catch (e) {
                console.warn('应用装甲运钞车（线下）可见性缓存失败:', e);
            }
        }

