            const tileX = clickX - translateX;
            const tileY = clickY - translateY;
            
            // 将像素坐标转换为游戏坐标
            const percentX = tileX / totalWidth;
            const percentY = tileY / totalHeight;
            
            const gameX = GAME_LEFT + percentX * (GAME_RIGHT - GAME_LEFT);
