        // 检查帮派攻击时间是否匹配
        function isGangAttackActive(timeRange) {
            try {
                // 获取当前游戏时间
                const timestamp = Math.floor(Date.now() / 1000);
                const currentHour = Math.floor(timestamp / 120) % 24;
                const currentMinute = Math.floor(timestamp / 2) % 60;
                const currentTime = currentHour * 60 + currentMinute; // 转换为分钟
                                
                // 解析时间范围 "04:00 - 12:00"
                const [startTime, endTime] = timeRange.split(' - ');
                const [startHour, startMinute] = startTime.split(':').map(Number);
                const [endHour, endMinute] = endTime.split(':').map(Number);
                
                const startTimeMinutes = startHour * 60 + startMinute;
                const endTimeMinutes = endHour * 60 + endMinute;
                                
                // 检查当前时间是否在范围内
                let isActive;
                if (startTimeMinutes <= endTimeMinutes) {
                    // 正常范围，如 04:00 - 12:00
                    isActive = currentTime >= startTimeMinutes && currentTime <= endTimeMinutes;
                } else {
                    // 跨天范围，如 22:00 - 06:00
                    isActive = currentTime >= startTimeMinutes || currentTime <= endTimeMinutes;
                }
                
                return isActive;
            } catch (error) {
                console.error('检查帮派攻击时间失败:', error, 'timeRange:', timeRange);
                return false;
            }
        }

